import events from './events';

describe('topics', () => {
  test('CreatedAccount', () => {
    const [uid, address] = events.decodeCreatedAccount(
      '',
    );

    expect(uid).toBe('8f2ddc84-e9d8-42f9-bb97-289571e1b648');
    expect(address).toBe('0x16db25945A4262529874c6eEF460E0C4b55618Fd');
  });

  test('FundedAccount', () => {
    const [uid, from, amount] = events.decodeFundedAccount(
      '',
    );

    expect(uid).toBe('f42f5e47-5a56-4f4f-87db-3f5c3aaece36');
    expect(from).toBe('0x3f87faD2Cf66d96c127A04e69692F502287F1D94');
    expect(amount.toString()).toBe('20000000000000000');
  });
});
